import React, { Component, Fragment } from 'react';
import Loaded from './Loaded';

// const App = (props) => {
// 	return (
// 		<h1>{ props.name }</h1>
// 	);
// }

class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			text: 'Hello World!',
			placeholder: 'Who are you?',
			value: '',
			hasLoaded: false
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleClick = this.handleClick.bind(this);
	}

	handleChange(params) {
		this.setState({
			input: params.target.value
		})
	}

	handleClick(params) {
		this.setState({
			hasLoaded: true
		});
	}

	render(props) {
		if (this.hasLoaded === true) {
			return (
				<Fragment>
					<h2>{ this.props.name }, { this.state.text }</h2>
					<input placeholder={ this.state.placeholder } onChange={ (params) => this.handleChange(params) } />
					<button onClick={ (params) => this.handleClick(params) } />
				</Fragment>
			);
		} else {
			return (
				<Fragment>
					<h2>Loading...</h2>
					<button onClick={(params) => this.handleClick(params)} />
				</Fragment>
			)
		}
	}

}

export default App;
