import React, { Component } from 'react';

// const App = (props) => {
// 	return (
// 		<h1>{ props.name }</h1>
// 	);
// }

class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			text: 'Hello World!',
			placeholder: 'Who are you?',
			value: ''
		};
	}

	handleChange = (e) => {
		this.setState({
			value: { e }
		})
	}

	render(props) {
		return (
			<React.Fragment>
				<h2>{ this.props.name }, { this.state.text }</h2>
				<input placeholder={ this.state.placeholder } onChange={ this.handleChange } />
			</React.Fragment>
		);
	}
}

export default App;
