// 		<h1>{ props.name }</h1>
// 	);
// }

class App extends Component {
	constructor(props) {
		super(props)

		this.state = {
			text: 'Hello World!',
			placeholder: 'Who are you?',
			value: '',
			hasLoaded: false
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleClick = this.handleClick.bind(this);
	}

	handleChange(params) {
		this.setState({
			input: params.target.value
		})
	}

	handleClick(params) {
		this.setState({
			hasLoaded: true
		});
	}

	render() {
		const isLoaded = this.state.hasLoaded;
		let load;

		return (
			<Fragment>
				{isLoaded ? (
					load = <Loaded onClick={ this.handleClick } name={ this.props.name } state={ this.state } onChange={ this.handleChange } />
				) : (
					load = <NotLoaded onClick={ this.handleClick}  />
				)}
			</Fragment>
		);
	}

